mapper.update:
update t_area_info
set area_name = #{areaName,jdbcType=VARCHAR},
  parent_id = #{parentId,jdbcType=VARCHAR},
  ex_status = #{exStatus,jdbcType=VARCHAR}
where area_id = #{areaId,jdbcType=VARCHAR}

mapper.select:
select area_id, area_name, parent_id, ex_status
from t_area_info
where area_id = #{areaId,jdbcType=VARCHAR}
